package Package17;

public class Wrapperclasses {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//String to Integer, Double, Boolean
		
		String Ac_num1 = "22";
		String Ac_num2 = "23";
		System.out.println(Integer.parseInt(Ac_num1)+Integer.parseInt(Ac_num2));
		
		//String to Double
		
		String Ac_num3 = "22.0";
		String Ac_num4 = "23.5";
		System.out.println(Double.parseDouble(Ac_num3)+Double.parseDouble(Ac_num4));
		
		//String to Boolean
		 String B1 ="true";
		 String B2 = "false";
		 String B3 = "selenium";
		 
		 System.out.println(Boolean.parseBoolean(B1));
		 System.out.println(Boolean.parseBoolean(B2));
		 System.out.println(Boolean.parseBoolean(B3));
		 System.out.println(B1);
		 System.out.println(B2);
		 System.out.println(B3);

		
		

	}

}
